import Head from 'next/head'
import Layout, { siteTitle } from '../../components/layout'
import Date from '../../components/date'
import { NotionRenderer } from "react-notion"
import { getAllPosts } from '../'
import { useRouter } from 'next/router'

const BLOG_URL = process.env.BLOG_URL
const CONTENT_API_KEY = process.env.CONTENT_API_KEY

export async function getPosts(slug) {
  const res = await fetch(
      `${BLOG_URL}ghost/api/v3/content/posts/slug/${slug}?key=${CONTENT_API_KEY}&fields=title,slug,reading_time,published_at,html`
      ).then((res) => res.json())

  console.log(res.posts)
  return res.posts[0]
}

export async function getStaticProps({ params: { slug } }) {
  const post = await getPosts(slug)

  // Find the current blogpost by slug
  // const post = posts.find((t) => t.slug === slug);

  // const blocks = await fetch(`https://notion-api.splitbee.io/v1/page/${post.id}`
  //               ).then((res) => res.json());

  return {
    props: {
     post,
    //  blocks,
    },
  }
}

export const getStaticPaths = () => {

  return {
    paths: [],
    fallback: true
  }
}

export default function Post({ post }) {

  const router = useRouter()

  if (router.isFallback) {
    return <h1>loading...</h1>
  }
  return (
    // <div>
    //   <h1>hello</h1>
    //   <div dangerouslySetInnerHTML={{ __html: post.html}}></div>
    // </div>
    <Layout page='post'>
      <Head>
        <title>{ siteTitle }</title>
      </Head>
      <div style={{ maxWidth: 768 }}>
        <div className='mb-10'>
          <h1 className='text-5xl font-semibold mb-4'>{post.title}</h1>
          <p className='text-sm text-gray-400'>
          <Date dateString={post.published_at} />
          </p>
        </div>
        <div dangerouslySetInnerHTML={{ __html: post.html}}></div>
    </div>
    </Layout> 
    );
}

