import Layout, { siteTitle } from '../../components/layout'
import Date from '../../components/date'
import { NotionRenderer } from "react-notion"
import { getAllPosts } from '../'

const { BLOG_URL, CONTENT_API_KEY } = process.env

export async function getPosts() {
  const res = await fetch(
      `${BLOG_URL}ghost/api/v3/content/posts/slug/${slug}?key=${CONTENT_API_KEY}&fields=title,slug,excerpt,reading_time,published_at,html`
      ).then((res) => res.json())

  return res.posts
}

export async function getStaticProps({ params: { slug } }) {
  // Get all posts again
  const posts = await getAllPosts();

  // Find the current blogpost by slug
  const post = posts.find((t) => t.slug === slug);

  // const blocks = await fetch(`https://notion-api.splitbee.io/v1/page/${post.id}`
  //               ).then((res) => res.json());

  return {
    props: {
     post,
    //  blocks,
    },
  };
}

const Slug = ({ post }) => (
  <Layout page='post'>
    <Head>
      <title>{ siteTitle }</title>
    </Head>
  <div style={{ maxWidth: 768 }}>
    <div className='mb-10'>
      <h1 className='text-5xl font-semibold mb-4'>{post.title}</h1>
      <p className='text-sm text-gray-400'>
        <Date dateString={post.date} />
      </p>
    </div>
    <div>
      {post.html}
    </div>
  </div>
  </Layout> 
  );

export async function getStaticPaths() {
    const posts = await getAllPosts();
    return {
      paths: posts.map((row) => `/blog/${row.slug}`),
      fallback: true,
    };
  }

export default Slug;
